script:
cur_frm.add_fetch("property", "property_name", "property_name");

frappe.ui.form.on("Item", {
  property: function(frm) {
    if (frm.doc.property) {
      frappe.model.get_value("Property", frm.doc.property, "property_name", function(value) {
        if (!frm.doc.item_code) {
          frm.set_value("item_code", value.property_name + "-");
        } else {
          var ic = frm.doc.item_code;
          var pos = ic.indexOf('-');
          if(pos > -1){
            /*We had the property set before... clear it*/
            ic = ic.slice(pos+1);
          }
          frm.set_value("item_code", value.property_name + "-" + ic);
          frm.set_value("item_name", value.property_name + "-" + ic);
          frm.set_value("description", value.property_name + "-" + ic);
        }
        frm.set_value("is_stock_item", 0);
      });

      frappe.model.get_value("Property", frm.doc.property, "trust_fund_account", function(value) {
        frm.set_value("income_account", value.trust_fund_account);
        frm.fields_dict["income_account"].df.reqd = 1;
      });

      frappe.model.get_value("Property", frm.doc.property, "property_cost_center", function(value) {
        frm.set_value("selling_cost_center", value.property_cost_center);
        frm.fields_dict["selling_cost_center"].df.reqd = 1;
      });
    } else {
      /*User deleted the property field contents. Reset everything....*/
      frm.set_value("item_code", "");
      frm.set_value("item_name", "");
      frm.set_value("is_stock_item", 1);
      frm.set_value("income_account", "");
      frm.set_value("selling_cost_center", "");
      frm.fields_dict["income_account"].df.reqd = 0;
      frm.fields_dict["selling_cost_center"].df.reqd = 0;
      frm.refresh_fields();
    }
  }
});
